        customerList.add(new Customer("Nguyễn Thái Hòa","1983-08-22","Nam Định", "data:image/jpeg;base64,"));
        customerList.add(new Customer("Trần Đăng Khoa","1983-08-17","Hà Tây", "https://www.google.com/imgres?imgurl=https%3A%2F%2Fd1hjkbq40fs2x4.cloudfront.net%2F2017-08-21%2Ffiles%2Flandscape-photography_1645.jpg&imgrefurl=https%3A%2F%2Fsnapshot.canon-asia.com%2Fvn%2Farticle%2Fviet%2Flandscape-photography-quick-tips-for-stunning-deep-focused-images&tbnid=wASLhvwyDli46M&vet=12ahUKEwiS4M2q0or9AhW8yXMBHaBMA-8QMygDegUIARC_AQ..i&docid=wJEFdG0FFBO98M&w=1024&h=683&q=%E1%BA%A3nh&ved=2ahUKEwiS4M2q0or9AhW8yXMBHaBMA-8QMygDegUIARC_AQ"));
